mapper.insert:

	SELECT NVL(MAX(bookmark_no),0) +1 AS bookmarkNo
	FROM bookmark

INSERT INTO bookmark
(
	bookmark_no,
	email,
	service_type,
	service_id
)
VALUES
(
	#{bookmarkNo}
	,#{email}
	,#{serviceType}
	,#{serviceId}
)

mapper.select:
SELECT * 
FROM bookmark
WHERE service_id = #{sId}
AND email = #{email}
AND service_type = #{serviceType}

mapper.insert:

	SELECT NVL(MAX(review_no),0) +1 AS reviewNo
	FROM review_board

INSERT INTO review_board
(
	REVIEW_NO,
	EMAIL,
	REVIEW_TITLE,
	REVIEW_CONTENT,
	REVIEW_DATE,
	SERVICE_TYPE,
	SERVICE_ID,
	VIEW_CNT,
	TOTAL_RATING,
	RATE_1,
	RATE_2,
	RATE_3,
	RATE_4,
	DELETE_STATE
)
VALUES
(
	#{reviewNo}
	,#{email}
	,#{reviewTitle}
	,#{reviewContent}
	,systimestamp
	,#{serviceType}
	,#{serviceId}
	,#{viewCnt}
	,#{totalRating}
	,#{rate1}
	,#{rate2}
	,#{rate3}
	,#{rate4}
	,#{deleteState}
)

mapper.select:
SELECT * 
FROM review_board
WHERE service_id = #{serviceId}
AND email = #{email}
AND service_type = #{serviceType}
