mapper.update:
		UPDATE moving_response 
        SET reserv_status = 1
        WHERE estimate_no = #{estimateNo}  
        AND email = #{email}

UPDATE moving_response
SET second_estimate_type = #{secondEstimateType},
    second_estimate_price = #{secondEstimatePrice}
WHERE estimate_no = #{estimateNo}

		UPDATE moving_response 
        SET reserv_status = 2
        WHERE estimate_no = #{estimateNo}  
        AND email = #{email}

		UPDATE moving_response 
