mapper.select:
SELECT * FROM UNTACT_ESTIMATE_MOVING WHERE email=#{email}
ORDER BY request_date DESC
ORDER BY estimate_type DESC, request_date DESC

SELECT * 
FROM bookmark
WHERE service_id = #{sId}
AND email = #{email}
AND service_type = #{serviceType}
