mapper.select:
select *
from house_product
where sigungu like #{sigungu}

select *
from bookmark
where service_type = 0
and email =
#{email}
and service_id = #{serviceId}

select *
from house_product p join house_detail d
on
p.product_id =
d.product_id
where p.house_type = #{houseType}
and
p.sale_type like #{saleType}
and

d.construction_year > #{constructionYear}
and p.price > #{minPrice} and p.price < #{maxPrice}
and
p.area_exclusive > #{minSize} and p.area_exclusive < #{maxSize}		

and
p.sigungu like #{sigungu}
