mapper.select:
SELECT count(*)
FROM login
WHERE email = #{email}

mapper.update:
UPDATE login
SET password = #{password}
WHERE email = #{email}

mapper.select:
SELECT email
FROM general_user
WHERE user_name = #{userName}
AND phone_number = #{phoneNumber}

SELECT email
FROM business
WHERE ceo_name = #{userName}
AND phone = #{phoneNumber}

mapper.update:
UPDATE general_user
SET profile_image = #{image}
WHERE email = #{email}
