mapper.insert:

	SELECT NVL(MAX(product_no),0) +1 AS productNo
	FROM
	used_product

INSERT INTO used_product
(
product_no,
email,
PRODUCT_NAME,
PRODUCT_CATEGORY,
PRODUCT_PRICE,
PRODUCT_INFO,
product_location,
is_sell,
views,
product_date
)
VALUES
(
#{productNo}
,#{email}
,#{productName}
,#{productCategory}
,#{productPrice}
,#{productInfo}
,#{productLocation}
,0
,0
,systimestamp
)


	SELECT NVL(MAX(image_no),0) +1 AS imageNo
	FROM used_images

INSERT INTO used_images
(
image_no,
product_no,
image
)
VALUES
(
#{imageNo}
,#{productNo}
,#{image}
)

mapper.update:
UPDATE used_images
SET
image
= #{image}
WHERE product_no = #{productNo}

mapper.insert:

	SELECT NVL(MAX(keyword_no),0) +1 AS keywordNo
	FROM
	used_keyword

INSERT INTO used_keyword
(
keyword_no,
email,
keyword,
keyword_location
)
VALUES
(
#{keywordNo}
,#{email}
,#{keyword}
,#{keywordLocation}
)
